import java.awt.*; import java.awt.event.*; import javax.swing.*; public class Calculator implements ActionListener{     JFrame f;JTextField tf;Font fnt;JButton[] b;     JButton c;JButton add;JButton sub;JButton mul;     JButton div;JButton eql;JButton clr;     double num1, num2;char operator;     Calculator(){         f = new JFrame("Calculator");         tf = new JTextField();         tf.setBackground(Color.CYAN);         f.setBackground(Color.BLACK);         fnt = new Font("Century",Font.BOLD,25);         tf.setFont(fnt);         b = new JButton[10];         for (int i = 0; i < 10; i++) {             b[i] = new JButton(String.valueOf(i));             b[i].addActionListener(this);         }         add = new JButton("+");sub = new JButton("-");         mul = new JButton("*");div = new JButton("/");         eql = new JButton("=");clr = new JButton("C");         c = new JButton("Close");add.addActionListener(this);         sub.addActionListener(this);mul.addActionListener(this);         div.addActionListener(this);eql.addActionListener(this);         clr.addActionListener(this);c.addActionListener(this);         f.setLayout(null);tf.setBounds(2,80,282,50);         b[1].setBounds(2,150,70,50);b[2].setBounds(72,150,70,50);         b[3].setBounds(142,150,70,50);add.setBounds(212,150,70,50);         b[4].setBounds(2,200,70,50);b[5].setBounds(72,200,70,50);         b[6].setBounds(142,200,70,50);sub.setBounds(212,200,70,50);         b[7].setBounds(2,250,70,50);b[8].setBounds(72,250,70,50);         b[9].setBounds(142,250,70,50);mul.setBounds(212,250,70,50);         clr.setBounds(2,300,70,50);b[0].setBounds(72,300,70,50);         eql.setBounds(142,300,70,50);div.setBounds(212,300,70,50);         c.setBounds(105,360,70,30);c.setBackground(Color.RED);         f.add(tf,BorderLayout.PAGE_START);         f.add(c);f.add(b[1]);f.add(b[2]);f.add(b[3]);f.add(add);f.add(b[4]);         f.add(b[5]);f.add(b[6]);f.add(sub);f.add(b[7]);f.add(b[8]);f.add(b[9]);         f.add(mul);f.add(clr);f.add(b[0]);f.add(eql);f.add(div);         f.setSize(300, 430);f.setVisible(true);      }      public void actionPerformed(ActionEvent e) {         String st = e.getActionCommand();         if (st.equals("Close")){             f.dispose();}        else if (Character.isDigit(st.charAt(0))) {             tf.setText(tf.getText() + st);         } else if (st.equals("+") || st.equals("-") || st.equals("*") || st.equals("/")) {             num1 = Double.parseDouble(tf.getText());             operator = st.charAt(0);tf.setText("");         } else if (st.equals("=")) {             num2 = Double.parseDouble(tf.getText());             double result = calculation();             tf.setText(String.valueOf(result));         } else if (st.equals("C")) {             tf.setText(""); num1 = num2 = 0;operator = '\0';         }      }      double calculation() {         switch (operator) {             case '+':                 return num1 + num2;             case '-':                 return num1 - num2;             case '*':                 return num1 * num2;             case '/':                 if (num2 != 0) {                     return num1 / num2;                 } else {                     tf.setText("Error: Cannot divide by zero");                     return 0;                 }             default:                 System.out.print("Error: Cannot divide by zero");                 return 0;         }     }     public static void main(String[] args){         new Calculator();     } }